import java.util.Scanner;public class Lab74 {    public static void main(String[] args) {        for(int i=0; i<2; i++ ){            Scanner num = new Scanner(System.in);            double a = 1 + (Math.random() * 3);            double b = 1 + (Math.random() * 3);            double c = 1 + (Math.random() * 3);            System.out.println((int) a + " " + (int) b + " " + (int) c);            System.out.print("Введіть числa: ");            int num1, num2, num3;            num1 = num.nextInt();            num2 = num.nextInt();            num3 = num.nextInt();            if ((int)a==num1 && (int)b==num2 && (int)c==num3){                System.out.println("Вітаю, Ви виграли!");                i++;            }else {                System.out.println("Неправильно");            }            System.out.println((int) a + " " + (int) b + " " + (int) c);        }    }}